interface IMatch {
    start: number;
    end: number;
}
/**
 * An array representing a fuzzy match.
 *
 * 0. the score
 * 1. the offset at which matching started
 * 2. `<match_pos_N>`
 * 3. `<match_pos_1>`
 * 4. `<match_pos_0>` etc
 */
type FuzzyScore = [score: number, wordStart: number, ...matches: number[]];
declare namespace FuzzyScore {
    /**
     * No matches and value `-100`
     */
    const Default: FuzzyScore;
    function isDefault(score?: FuzzyScore): score is [-100, 0];
}

export { FuzzyScore, type IMatch };
